class Solution {

    public int findNumberOfLIS(int[] nums) {
		// first coordinate compression
        int n = nums.length;
        Integer[] indexes = new Integer[n];
        for (int i = 0; i < n; i++) {
            indexes[i] = i;
        }

        Arrays.sort(indexes, Comparator.comparingInt(i -> nums[i]));

        int coordinate = 1;
        int[] coordinates = new int[n];
        for (int i = 0; i < n; i++) {
            while (i < n - 1 && nums[indexes[i]] == nums[indexes[i + 1]]) {
                coordinates[indexes[i++]] = coordinate;
            }
            coordinates[indexes[i]] = coordinate++;
        }

		// then using compressed coordinates to build the tree
        SegmentTree tree = new SegmentTree(coordinate);
        for (int i = 0; i < n; i++) {
            Sequence s = tree.query(coordinates[i] - 1);
            tree.update(coordinates[i], new Sequence(s.length + 1, s.count));
        }
        return tree.query(coordinate - 1).count;
    }

    private static class SegmentTree {

        private final int n;
        private final Sequence[] tree;

        SegmentTree(int n) {
            this.n = n;
            this.tree = new Sequence[n * 2];
            Arrays.fill(tree, Sequence.EMPTY);
        }

        void update(int idx, Sequence s) {
            idx += n;
            tree[idx] = tree[idx].merge(s);

            while (idx > 1) {
                tree[idx >> 1] = tree[idx].merge(tree[idx ^ 1]);
                idx >>= 1;
            }
        }

        Sequence query(int r) {
            int l = n;
            r += n;

            Sequence s = Sequence.EMPTY;
            while (l <= r) {
                if ((l & 1) == 1) {
                    s = s.merge(tree[l++]);
                }
                if ((r & 1) == 0) {
                    s = s.merge(tree[r--]);
                }
                l >>= 1;
                r >>= 1;
            }
            return s;
        }
    }

    private static class Sequence {
        final int length;
        final int count;

        Sequence(int length, int count) {
            this.length = length;
            this.count = count;
        }

        Sequence merge(Sequence other) {
            if (other.length > this.length) {
                return other;
            }
            if (other.length < this.length) {
                return this;
            }
            return new Sequence(length, this.count + other.count);
        }

        static final Sequence EMPTY = new Sequence(0, 1) {
            Sequence merge(Sequence other) {
                return other.length > this.length ? other : this;
            }
        };
    }
}
