class Solution {
    public int findNumberOfLIS(int[] nums) {
		// first the coordinate compression
        int n = nums.length;
        Integer[] indexes = new Integer[n];
        for (int i = 0; i < n; i++) {
            indexes[i] = i;
        }

        Arrays.sort(indexes, Comparator.comparingInt(i -> nums[i]));

        int coordinate = 0;
        int[] coordinates = new int[n];
        for (int i = 0; i < n; i++) {
            while (i < n - 1 && nums[indexes[i]] == nums[indexes[i + 1]]) {
                coordinates[indexes[i]] = coordinate;
                i++;
            }
            coordinates[indexes[i]] = coordinate++;
        }

		// then using compressed coordinates in a BIT
        BinaryIndexedTree bit = new BinaryIndexedTree(coordinate);
        for (int i = 0; i < n; i++) {
            Sequence s = bit.query(coordinates[i] - 1);
            bit.update(coordinates[i], new Sequence(s.length + 1, s.count));
        }

        return bit.query(coordinate - 1).count;
    }

    private static final class BinaryIndexedTree {
        private final Sequence[] a;

        BinaryIndexedTree(int n) {
            this.a = new Sequence[n + 1];
        }

        void update(int idx, Sequence s) {
            idx++;

            while (idx < a.length) {
                if (a[idx] == null || a[idx].length < s.length) {
                    a[idx] = s;
                } else if (a[idx].length == s.length) {
                    a[idx] = new Sequence(s.length, s.count + a[idx].count);
                }
                idx += idx & -idx;
            }
        }

        Sequence query(int idx) {
            idx++;
            Sequence s = new Sequence(0, 1);

            while (idx > 0) {
                if (a[idx] != null) {
                    if (a[idx].length > s.length) {
                        s = a[idx];
                    } else if (a[idx].length == s.length) {
                        s = new Sequence(s.length, s.count + a[idx].count);
                    }
                }
                idx -= idx & -idx;
            }

            return s;
        }
    }

    private static final class Sequence {
        private final int length;
        private final int count;

        Sequence(int length, int count) {
            this.length = length;
            this.count = count;
        }
    }
}
