        public static int FindNumberOfLIS(int[] nums)
        {

            int[] lisLength = new int[nums.Length];
            int[] countOfLIS = new int[nums.Length];
            int longestSequenceLength = 0;
            int numberOfLIS = 0;

            for(var right = 0; right < nums.Length; right++)
            {
                lisLength[right] = 1;
                countOfLIS[right] = 1;

                for(var left = right - 1; left >= 0; left--)
                {
                    if(nums[left] < nums[right])
                    {
                        if (lisLength[right] < lisLength[left] + 1)
                        {
                            lisLength[right] = lisLength[left] + 1;
                            countOfLIS[right] = countOfLIS[left];
                        }
                        else if(lisLength[right] == lisLength[left] + 1)
                        {
                            countOfLIS[right] += countOfLIS[left];
                        }
                    }
                }

                if (lisLength[right] > longestSequenceLength)
                {
                    longestSequenceLength = lisLength[right];
                    numberOfLIS = countOfLIS[right];
                }
                else if (lisLength[right] == longestSequenceLength)
                    numberOfLIS += countOfLIS[right];                                                 
            }

            return numberOfLIS;
        }

