class Solution:
    def findNumberOfLIS(self, nums: List[int]) -> int:
        self.memo = {}
        max_len, count = 0, 0
        for i in range(len(nums)):
            curr_max, curr_count = self.helper(i, nums)
            if curr_max == max_len:
                count += curr_count
            elif curr_max > max_len:
                max_len = curr_max
                count = curr_count
        return count

    def helper(self, i, nums):
        if i in self.memo:
            return self.memo[i]

        max_length = 1; count = 0

        for j in range(i+1, len(nums)):
            if nums[j] > nums[i]:

                max_length_j, count_j = self.helper(j, nums)

                if max_length == 1 + max_length_j:
                    count += count_j

                elif 1 + max_length_j > max_length:
                    count = count_j
                    max_length = 1 + max_length_j

        count = max(1, count)
        self.memo[i] = (max_length, count)
        return self.memo[i]

