var findNumberOfLIS = function(nums) {
    // saftey check that nums has length and is not null
    if (nums === null || nums.length === 0) {
        return 0;
    }

    // initialise array to record the length of the longest increasing subsequence at each i
    const LISRecord = new Array(nums.length).fill(1);

    // initialise array to record the number of LIS of length LISRecord[i]
    const numberOfLISRecord = new Array(nums.length).fill(1);

    // iterate i from 0 to nums.length
    for(let i=0; i<nums.length; i++) {
        // for each i in nums, iterate j from 0 up to i-1
        for(let j=0; j<i; j++) {

            // if nums[i] is greater than nums[j]
            // i.e. putting nums[i] after nums[j] would continue a increasing subsequence
            if (nums[i] > nums[j]) {

                // if the longest possible subsequence up to nums[i] is less than that up to nums[j]+1
                // which it will be in the first iteration at least because all of LISRecord is initialised
                // to 1
                if (LISRecord[i] < LISRecord[j] + 1) {
                    // since nums[i] is less than nums[j] the possible longest increasing subsequence to
                    // i is atleast one longer
                    LISRecord[i] = LISRecord[j] + 1;
                    // and how ever many ways there were to make LISRecord[j] 
                    // there are an equal many ways to make LISRecrod[i]
                    numberOfLISRecord[i] = numberOfLISRecord[j];
                }
                // if i is a LIS one larger than j - meaning the longest possible subsequence up
                // to nums[i] is one larger than nums[j+1]
                else if(LISRecord[i] === LISRecord[j] + 1) {
                    // then the amount of ways of of making LISRecord[i] is the number of ways of
                    // making LISRecord[i] that we have already calculated plus
                    // the number of ways of making LISRecord[j] since
                    // nums[j] is less than nums[i] and thus all the sequences that end
                    // at nums[j] can be extended by one to end at nums[i]
                    numberOfLISRecord[i] = numberOfLISRecord[i] + numberOfLISRecord[j];
                }
            }
        }
    }

    // then using the information we have tabulated it is easy to to find the answer
    // the number of ways of creating the longest increasing subsequence is
    // the sum of the values in numberOfLISRecord at each i, where i is the largest
    // number in LISRecord
    // i.e. LISRecord - shows us the LIS we can make - and numberOFLISRecord tells us
    // how many ways to make that
    // so for each occurrence of the LIS in LISrecord, we just get the corresponding number
    // from numberOfLISRecord (recorded at the same index)
    let longestLIS = Math.max(...LISRecord, 0);
    let occurrencesOfLongestLIS = 0;
    for(let index=0; index<numberOfLISRecord.length; index++) {
        if (LISRecord[index] === longestLIS) {
            occurrencesOfLongestLIS = occurrencesOfLongestLIS + numberOfLISRecord[index];
        }
    }

    // console.log(nums);
    // console.log(LISRecord);
    // console.log(numberOfLISRecord);
    return occurrencesOfLongestLIS;
};

