class Solution {
    public int findNumberOfLIS(int[] nums) {
        int[] dp = new int[nums.length], counts = new int[nums.length];
        int max = 1;
        for (int i = 0; i < nums.length; i++) {
            int temp = 1;
            for (int j = 0; j < i; j++) {
                if (nums[j] < nums[i]) {
                    temp = Math.max(temp, dp[j] + 1); // get the temp max length until this position
                }
            }
            for (int j = 0; j < i; j++) {
                if (nums[j] < nums[i] && temp == dp[j] + 1) {
                    counts[i] += counts[j]; // get the ways to get this temp max length
                }
            }
            dp[i] = temp;
            counts[i] = Math.max(counts[i], 1);
            max = Math.max(max, temp); // get the global max
        }
        int result = 0;
        for (int i = 0; i < dp.length; i++) {
            if (dp[i] == max) {
                result += counts[i]; // if we get global max, add the ways to get that length on our result
            }
        }
        return result;
    }
}
