class Solution {
public:
    int findNumberOfLIS(vector<int>& nums) {
        int ans = INT_MIN, n = nums.size();
        vector<pair<int, int>> dp (n, {1, 1});

        for(int i = 0; i < n; i++) {
            for(int j = 0; j < i; j++) {
                if(nums[i] > nums[j]) {
                    if(dp[i].first < dp[j].first + 1) dp[i].second = 0;

                    dp[i].first = max(dp[i].first, dp[j].first + 1);
                    if(dp[i].first <= dp[j].first + 1) dp[i].second += dp[j].second;

                    ans = max(ans, dp[i].first);
                }
            }
        }

        if(ans == INT_MIN) return nums.size(); 
        int res = 0;

        for(auto [sub, count]: dp) 
            if(sub == ans) res += count;


        return res;
    }
};
