# Code
```
class Solution {
public:
    int findNumberOfLIS(vector<int>& nums) {
        int n = nums.size();

        // Create DP array to track smallest ending element of increasing subsequences
        vector<int> dp(n+1, INT_MAX);

        // Create 2D vector to store information about increasing subsequences of different lengths
        vector<vector<pair<int,int>>> LISbylen(n+1);//count,last ele of that len

        // Initialize base case for DP and LISbylen
        dp[0] = INT_MIN;
        LISbylen[0] = {{1, INT_MIN}};

        int max_len = 1; // Track the maximum length of increasing subsequences

        // Iterate through each element in the input array
        for(auto &ele: nums){
            // Find the length of the LIS ending at ele
            int len = lower_bound(dp.begin(), dp.end(), ele) - dp.begin();
            dp[len] = ele; // Update the DP array

            int ele_count = 0; // Count of subsequences ending at ele

            // Iterate through previous subsequences of length len-1
            for(auto &[count, last]: LISbylen[len-1]){
                if(ele > last) {
                    ele_count += count; // Increment count for valid extensions
                }
            }

            // Store the information about the new subsequence ending at ele
            LISbylen[len].push_back({ele_count, ele});

            // Update the maximum length of increasing subsequences
            max_len = max(max_len, len);
        }

        int num_lis = 0; // Total number of Longest Increasing Subsequences

        // Count the number of LIS by summing counts of subsequences of maximum length
        for(auto &[count, last]: LISbylen[max_len]){
            num_lis += count;
        }

        return num_lis;
    }
};

