class Solution {
public:
    int findNumberOfLIS(vector<int>& nums) {
        int n=nums.size();
        vector<int>res(n,1);  //stores len
        vector<int>count(n,1);  //stores count
        int mxlen=1;
        for(int i=0;i<n;i++){
            for(int j=i-1;j>=0;j--){
                if(nums[i]<nums[j]) continue;
                if(nums[i]>nums[j]){
                    if(res[j]+1 > res[i]){
                        res[i]=res[j]+1;
                        count[i]=count[j];
                    }
                    else if(res[j]+1==res[i]){
                        count[i]+=count[j];

                    }    
                }
            }
            mxlen=max(mxlen,res[i]);
        }
        int no_of_lis=0;
        for(int i=0;i<n;i++){
            if(res[i]==mxlen){
                no_of_lis+=count[i];
            }
        }
        return no_of_lis;
    }
};
