class Solution:
    def findLengthOfLCIS(self, nums: List[int]) -> int:
        if not nums:
            return 0
        if len(nums)==1:
            return 1
        st = []
        max_len = float('-inf')
        for i in range(len(nums)):
            if not st:
                st.append(nums[i])
            elif st and st[-1]<nums[i]:
                st.append(nums[i])
            elif st and st[-1]>=nums[i]:
                st = []
                st.append(nums[i])
            max_len = max(max_len,len(st))
        return max_len
