class Solution:
    def findLengthOfLCIS(self, nums: List[int]) -> int:
        # Base case: empty list or single element list
        if len(nums) == 0 or len(nums) == 1:
            return len(nums)

        # Initialize variables to keep track of current length and max length
        cur_len = 1  # current length of increasing subsequence
        max_len = 1  # maximum length of increasing subsequence

        # Iterate through the list starting from the second element
        for i in range(1, len(nums)):
            # If the current element is greater than the previous element, it is part of the increasing subsequence
            if nums[i] > nums[i-1]:
                cur_len += 1  # increase the length of the subsequence
                max_len = max(max_len, cur_len)  # update the maximum length if necessary
            else:
                cur_len = 1  # reset the length of the subsequence if the current element is not greater than the previous element

        return max_len

