pub struct Solution {}

impl Solution {
    pub fn find_length_of_lcis(nums: Vec<i32>) -> i32 {
        let mut max = 0;
        let accum = nums
            .windows(2)
            .fold(1, |accum, w| {
                if w[0] < w[1] {
                    accum + 1
                } else {
                    if accum > max {
                        max = accum;
                    }
                    1
                }
            });

        max.max(accum)
    }
}

