class Solution {
    fun findLengthOfLCIS(nums: IntArray): Int {
        // sanity check
        if(nums.isEmpty()) return 0

        val nNums = nums.size

        var longest = 1
        var len = 1

        for(i in 1 until nNums){
            if(i == 0 || nums[i - 1] < nums[i]){
                longest = maxOf(longest, ++len)
            }else{
                len = 1
            }
        }

        return longest
    }
}
