	if( n == 0 ) return 0;

	vector<int> dp( n, 0 );
	dp[0] = 1;

	int longest = 1;
	for( int i=1; i < n; i++ ) {
		if( nums[i] > nums[i-1] )
			dp[i] = dp[i-1] + 1;
		else
			dp[i] = 1;
		longest = max( longest, dp[i] );
	}
	return longest;
}
