public int findLengthOfLCIS(int[] nums) {
	if (nums == null || nums.length == 0) {
		return 0;
	}

	int left = 0;
	int right = 1;
	int ans = 0;
	while (right < nums.length) {
		if (nums[right] <= nums[right - 1]) {
			ans = Math.max(ans, right - left);
			left = right;
		}
		right++;
	}

	ans = Math.max(ans, right - left);

	return ans;
}
