class Solution {
public:
    int findLengthOfLCIS(vector<int>& nums) {
        volatile int l = nums.size() - 1;
        volatile int m = 1,c = 1;
        for (int i = 0; i < l ; i++){
            if(nums[i] < nums[i+1])
                c += 1;
            else{
                m = max(c,m);
                c = 1;
            }
        }
        m = max(c,m);
        return m;
    }
};
