class Solution {
public:
    int findLengthOfLCIS(vector<int>& nums) {
        if(nums.size()==1)  return 1;
        int max=INT_MIN;
        int start=0;
        int end=0;

        while(end<nums.size()-1){   
            if(nums[end+1]>nums[end])   end++;
            else{
                if(max<end-start+1) max=end-start+1;
                start=end+1;
                end++;
            }
        }
        if(max<end-start+1 && end==nums.size()-1)  max=end-start+1;
        return max;
    }
};
