class Solution {
    int[][] dirs = {{0,1}, {0, -1}, {1, 0}, {-1, 0}};
    public int cutOffTree(List<List<Integer>> forest) {
        // Sort trees by height
        PriorityQueue<int[]> pq = new PriorityQueue<int[]>( new Comparator<int[]>(){
            public int compare(int[] p1, int[] p2){
                return p1[2] - p2[2];
            }
        });

        // Add trees in the pq
        for (int r = 0; r < forest.size(); ++r) 
            for (int c = 0; c < forest.get(0).size(); ++c) {
                int height = forest.get(r).get(c);
                if (height >0) pq.add(new int[]{r, c, height});
            }

        int last_i = 0, last_j = 0;
        int result = 0;

        // Visit shorter trees first
        while (!pq.isEmpty()){
            int[] new_step = pq.poll();
            int step = bfs(forest, last_i, last_j, new_step[0], new_step[1]);
            if (step == -1) return -1;
            result += step;
            last_i = new_step[0];
            last_j = new_step[1];
        }
        return result;

    }

    // Using BFS to record shortest dist between two trees
    private int bfs(List<List<Integer>> forest, int start_i, int start_j, int end_i, int end_j){

        Queue<int[]> queue = new LinkedList();
        int m = forest.size(), n = forest.get(0).size();
        boolean[][] visited = new boolean[m][n];

        queue.add(new int[]{start_i, start_j});
        int level = 0;
        while(!queue.isEmpty()){
            int size = queue.size();
            for (int i=0; i<size; i++){
                int[] curr = queue.poll();
				// Target is found. Return the level of BFS traversal as steps
                if (curr[0] == end_i && curr[1] == end_j)
                    return level;
                for (int j=0; j<4; j++){
                    int next_i = curr[0]+dirs[j][0];
                    int next_j = curr[1]+dirs[j][1];
                    if (inBounds(forest, next_i , next_j) && ! visited[next_i][next_j]){
                        visited[next_i][next_j] = true;
                        queue.offer(new int[]{ next_i, next_j});
                    }
                }
            }
            level++;
        }
        return -1;
    }

    // Return false if out of forest or meet obstacles
    private boolean inBounds(List<List<Integer>> forest, int i, int j){
        return (i >=0 && i<=forest.size()-1 && j >=0 && j<=forest.get(0).size()-1 && forest.get(i).get(j) != 0);
    }

}
