class Solution:
  def processQueue(self, queue, selfVisited, targetVisited):
    for _ in range(len(queue)):
        cr, cc = queue.popleft()
        if (cr, cc) in targetVisited:
          return True

        for d in self.dir:
          nr, nc = cr + d[0], cc + d[1]
          if nr >= self.R or nc >= self.C or nr < 0 or nc < 0:
            continue
          if self.fr[nr][nc] == 0:
            continue
          if (nr, nc) in selfVisited:
            continue
          queue.append((nr, nc))
          selfVisited.add((nr,nc))

    return False

  def findNext(self, pos, target):
    beginque= deque()
    endque= deque()
    beginque.append(pos)
    endque.append(target)
    step = 0
    visitedFromBegin = set()
    visitedFromBegin.add(pos)
    visitedFromEnd = set()
    visitedFromEnd.add(target)

    while beginque or endque:
      if self.processQueue(beginque, visitedFromBegin, visitedFromEnd):
        return step
      step += 1

      if self.processQueue(endque, visitedFromEnd, visitedFromBegin):
        return step
      step += 1

    return -1


  def cutOffTree(self, forest: List[List[int]]) -> int:
    self.dir = [(0,1), (1,0), (0,-1), (-1,0)]
    self.fr = forest
    self.R = len(forest)
    self.C = len(forest[0])

    trees = []
    treePos = dict()
    for r in range(self.R):
      for c in range(self.C):
        if self.fr[r][c] > 0:
          trees.append(self.fr[r][c])
          treePos[self.fr[r][c]] = (r,c)

    trees.sort(reverse=True)

    ret = 0
    pos = (0,0)
    while trees:
      nextTree = trees.pop()
      if nextTree == 1:
        continue
      steps = self.findNext(pos, treePos[nextTree])
      if steps == -1:
        return -1
      ret += steps
      pos = treePos[nextTree]

    return ret
