solutions.content:
Approach -> Apply BFS from src node (initially (0,0))  to the smallest node with value >1. If there is no path then return -1. Else continue the iteration until you have cut all the trees in ascending order or you can't find a path anymore.

class Solution {
public:
    bool is_valid(int x,int y,int n,int m,vector<vector<int>> &mat)
    {
        if(x<0 || x>=n || y<0 || y>=m || mat[x][y]==0)
            return false;

        return true;
    }
    int cutOffTree(vector<vector<int>>& mat) {

        int n=mat.size();
        int m=mat[0].size();
        if(n==0)
            return 0;

        if(mat[0][0]==0)
            return -1;
        int curr_x=0,curr_y=0;
        int ans=0;
        priority_queue<pair<int,pair<int,int>>> pq;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(mat[i][j]>1)
                pq.push({-mat[i][j],{i,j}});
            }
        }
        bool vis[n][m];
        while(!pq.empty())
        {
            pair<int,pair<int,int>> t=pq.top();
            pq.pop();
            int final_x=t.second.first;
            int final_y=t.second.second;
            queue<pair<int,int>> q;
            q.push({curr_x,curr_y});
            memset(vis,false,sizeof vis);
            vis[curr_x][curr_y]=true;
            int lvl=0;
            int arr[]={1,0,-1,0};
            int brr[]={0,1,0,-1};
            bool ok=false;
            while(!q.empty())
            {
                int si=q.size();
                for(int i=0;i<si;i++)
                {
                    pair<int,int> s=q.front();
                    q.pop();
                    if(s.first==final_x && s.second==final_y)
                    {
                        ans+=lvl;
                        ok=true;
                        break;
                    }
                    for(int i=0;i<4;i++)
                    {
                       int x=s.first+arr[i];
                       int y=s.second+brr[i];
                       if(is_valid(x,y,n,m,mat))
                       {
                          if(vis[x][y]==false)
                          {
                            vis[x][y]=true;
                            q.push({x,y});
                          }
                       }
                    }
                }
                if(ok)
                    break;

                lvl++;
            }
            if(ok==false)
                return -1;

            curr_x=final_x;
            curr_y=final_y;

        }

        return ans;
    }
};

