class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        if forest[0][0] == 0 :
            return -1
        m = len(forest)
        n = len(forest[0])
        def distance(node1, node2):
            now = [node1]
            soon = []
            expanded = set()
            manhattan = abs(node1[0] - node2[0]) + abs(node1[1] - node2[1])
            detours = 0
            while True:
                if len(now) == 0:
                    now = soon
                    soon = []
                    detours += 1
                node = now.pop()
                if node == node2:
                    return manhattan + 2 * detours
                if node not in expanded:
                    expanded.add(node)
                    x, y = node
                    if x - 1 >= 0 and forest[x - 1][y] >= 1:
                        if x > node2[0]:
                            now.append((x - 1, y))
                        else:
                            soon.append((x - 1, y))
