class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        m, n = len(forest), len(forest[0])
        if forest[0][0] == 0:
            return -1
        # Have priority queue that stores (height,x,y) for each tree in order of min to max.
        # Use BFS to find the next available smallest height in priority queue.
        heap = []

        for i in range(m):
            for j in range(n):
                if forest[i][j] > 1:
                    heappush(heap, (forest[i][j], i, j))


        def bfs(sx, sy, tx, ty):

            visited = set()
            visited.add((sx, sy))

            queue = deque()
            queue.append((sx, sy))
            d = 0
            while queue:
                for _ in range(len(queue)):
                    cx, cy = queue.popleft()
                    if cx == tx and cy == ty:
                        forest[cx][cy] = 1
                        return d

                    for nx, ny in ((cx+1, cy), (cx-1, cy), (cx, cy-1), (cx, cy+1)):
                        if 0 <= nx < m and 0 <= ny < n and forest[nx][ny] != 0 and(nx, ny) not in visited:
                            queue.append((nx, ny))
                            visited.add((nx, ny))

                d += 1
            return -1

        ans = 0
        prev_x, prev_y = 0, 0
        while heap:
            _, x, y = heappop(heap)

            step = bfs(prev_x, prev_y, x, y)
            if step == -1:
                return -1
            ans += step
            prev_x, prev_y = x, y
        return ans

