class Solution {
    public int cutOffTree(List<List<Integer>> forest) {
        if (forest == null || forest.size() == 0 || forest.get(0).size() == 0) {
            return -1;
        }

        int[][] matrix = new int[forest.size()][forest.get(0).size()];
        List<Integer> trees = new ArrayList<>();

        for (int i = 0; i < forest.size(); i++) {
            List<Integer> list = forest.get(i);
            for (int j = 0; j < list.size(); j++) {
                matrix[i][j] = list.get(j);
                if (matrix[i][j] > 1) {
                    trees.add(matrix[i][j]);
                }
            }
        }

        Collections.sort(trees);
        int row = 0, col = 0;
        int steps = 0;

        for (Integer tree: trees) {
            boolean[][] visited = new boolean[matrix.length][matrix[0].length];
            int[] result = cut(matrix, visited, row, col, tree);

            if (result[0] == 1) {
                steps += result[1];
                row = result[2];
                col = result[3];
            } else {
                return -1;
            }
        }
        return steps;
    }

    private int[] cut(int[][] matrix, boolean[][] visited, int row, int col, int tree) {
        if (matrix[row][col] == tree) {
            matrix[row][col] = 1;
            return new int[] {1, 0, row, col};
        }

        Queue<String> q = new LinkedList<>();
        q.add(row + "_" + col);
        visited[row][col] = true;
        q.add(null);

        int steps = 0;

        while (!q.isEmpty()) {
            String current = q.remove();
            if (current == null) {
                if (!q.isEmpty()) {
                    q.add(null);
                }
                steps++;
                continue;
            }

            String[] parts = current.split("_");
            row = Integer.parseInt(parts[0]);
            col = Integer.parseInt(parts[1]);
            if (matrix[row][col] == tree) {
                return new int[] {1, steps, row, col};
