Idea: Find the order of trees you need to find, BFS between each of the two points to find shortest path between each consective pair of trees (ie shortest path between x1 and x2, then x2 and x3 where xi comes before xj if i < j).

```
class Solution {
    class Triple{
        int val;
        int row;
        int col;
        public Triple (int val, int row, int col){
            this.val = val;
            this.row = row;
            this.col = col;
        }
    }
    class TripleComparator implements Comparator<Triple>{
        public int compare(Triple a, Triple b){
            return a.val - b.val;
        }
    }
    public int cutOffTree(List<List<Integer>> forest) {
        List<Triple> trees = new ArrayList<>();
        for(int i = 0; i < forest.size(); i++){
            for(int j = 0; j < forest.get(i).size(); j++){
                if(forest.get(i).get(j) > 1) trees.add(new Triple(forest.get(i).get(j), i, j));
            }
        }
        Collections.sort(trees, new TripleComparator());
        int[][] directions = new int[][] {{1,0}, {-1,0}, {0,-1}, {0,1}};
        int totalDistance = 0;
        Triple pastTree = new Triple(0,0,0);
        for(int i = 0; i < trees.size(); i++){
            Triple currTree = trees.get(i);
            int distance = findNextTree(forest, pastTree.row, pastTree.col, currTree.row, currTree.col, directions);
            if(distance == -1) return -1;
            totalDistance += distance;
            pastTree = currTree; 
        }
        return totalDistance; 
    }
    //number of edges between nodes, else -1 if unreachable 
    //bfs gives shortest path from one node to another we do this for every pair of nodes
    private int findNextTree(List<List<Integer>> forest, int sr, int sc, int dr, int dc, int[][] directions){
        boolean[][] visited = new boolean[forest.size()][forest.get(0).size()];
        Queue<Triple> bfsQ = new LinkedList<>();
        bfsQ.offer(new Triple(forest.get(sr).get(sc), sr, sc));
        int level = -1;
        while(!bfsQ.isEmpty()){
            int qSize = bfsQ.size();
            while(qSize > 0){
                qSize--;
                Triple currNode = bfsQ.poll();
                if(currNode.row == dr && currNode.col == dc) {
                    level++; 
                    return level;
                }
                for(int[] dir : directions){
                    int nr = dir[0] + currNode.row;
                    int nc = dir[1] + currNode.col;
                    if(nr < 0 || nr > forest.size() - 1 || nc < 0 || nc > forest.get(nr).size()-1) continue;
                    if(visited[nr][nc] || forest.get(nr).get(nc) < 1) continue;
                    bfsQ.offer(new Triple(forest.get(nr).get(nc), nr, nc));
                    visited[nr][nc] = true; 
                }
            }
            level ++; 
        }
        return -1; 
    }


}
