class Solution {
    public int cutOffTree(List<List<Integer>> forest) {

        //1) put all tree's into a list
        //2) sort the tree list by smallest
        //3) from idx (0,0) cut down all the trees by getting the next smallest tree from the list
        //4) call bfs to count the steps from current starting index until the next smallest tree

        //so if you can make it from the beginning to the smallest tree. count the steps at that tree as the new starting point to get to the next smallest

        List<int[]> trees = new ArrayList<>();

        int R = forest.size(), C = forest.get(0).size();

        //1)
        for(int r = 0; r < R; r++){

            for(int c = 0; c < C; c++){

                int treeHeight = forest.get(r).get(c);

                if(treeHeight < 2) continue;

                trees.add(new int[]{ r, c, treeHeight});
            }
        }

        //2)
        Collections.sort(trees, (tree1, tree2) -> { return tree1[2] - tree2[2]; });

        int steps = 0;

        //3)
        int[] startTree = new int[] { 0, 0 };

        for(int[] nextTree : trees){

            int stepsToNextTree = getStepsToNextTree(forest, startTree, nextTree, R, C);

            //we could not make it to the next tree, therefore not all trees can be cut down
            if(stepsToNextTree < 0) return -1;

            steps += stepsToNextTree;

            //the current next tree becomes the new start tree

            startTree = nextTree;
        }

        return steps;
    }

    private int[][] directions = new int[][]{ {0, 1}, {0, -1}, {-1, 0}, {1, 0} };

    private int getStepsToNextTree(List<List<Integer>> forest, int[] startTree, int[] nextTree, int R, int C){

        //implelent bfs to move around the board taking steps to reach the next tree
        //do not move back to a visited tree within this function
        boolean[][] seen = new boolean[R][C];

        seen[startTree[0]][startTree[1]] = true;

        Queue<int[]> toVisit = new LinkedList<>();

        toVisit.offer(startTree);

        int steps = 0;

        while(!toVisit.isEmpty()){

            //pop elements off the q and process level by level
            int size = toVisit.size();

            for(int i = 0; i < size; i++){

                int[] visitedTree = toVisit.poll();

                //if we have reached the next tree we can stop
                if(visitedTree[0] == nextTree[0] && visitedTree[1] == nextTree[1]) return steps;

                //search surrounding cells for a connection to the next tree

                for(int[] dir : directions){

                    int r = visitedTree[0] + dir[0];
                    int c = visitedTree[1] + dir[1];

                    if(r < R && c < C && r >= 0 && c >= 0 && !seen[r][c] && forest.get(r).get(c) > 0){

                        toVisit.offer(new int[]{r, c});
                        seen[r][c] = true;
                    }
                }
            }

            //each time we visit a tree, we find all surrounding connected trees - however we only move one step when we visit that tree
            steps++;
        }

        //if we have proccessed all surrounding cells as possible but not found the nextTree that means we will cannot connect to it
        return -1;
    }
}
