    def find_tree(self, target, r, c, forest, steps):
        queue = [(r,c,0)]
        seen = set()
        while queue:
            r,c,step = queue.pop(0)
            if 0 <= r <= len(forest)-1 and 0 <= c <= len(forest[0]) - 1 and (r,c) not in seen and forest[r][c] >= 1:
                if forest[r][c] == target:
                    steps[0] += step
                    return [r,c]
                seen.add((r,c))
                queue.append((r+1,c,step+1))
                queue.append((r-1,c,step+1))
                queue.append((r,c+1,step+1))
                queue.append((r,c-1,step+1))

