        g = sorted((forest[i][j],i,j) for i in range(row) for j in range(col) if forest[i][j] > 1)
        ans = 0
        x = y = 0
        while g:
            h,i,j = g.pop(0)
            move = bfs(x,y, h) 
            if move == -1:
                return -1 
            ans += move 
            x,y = i,j 
