public class Solution {
    public int CutOffTree(IList<IList<int>> forest) {

        // The main idea is that we will put all the trees in minHeap in order to
        // cut them from lower height to higher.
        // Then, to find out distance between two adjacent trees we'll use BFS
        const int BLOCK = 0;

        var directions = new (int dR, int dC)[] { (-1, 0), (0, 1), (1, 0), (0, -1) };

        int rows = forest.Count;
        int columns = forest[0].Count;

        PriorityQueue<(int row, int column), int> minHeap = new();

        for (int row = 0; row < rows; row++)
            for (int column = 0; column < columns; column++)
            {
                int height = forest[row][column];

                if (height > 1) minHeap.Enqueue((row, column), height);
            }

        int totalSteps = 0;
        (int row, int column) source = (0, 0);

        while (minHeap.TryDequeue(out var target, out _))
        {
            int stepsTaken = minSteps(source.row, source.column, target.row, target.column);

            if (stepsTaken == -1) return -1;

            totalSteps += stepsTaken;

            source = target;
        }

        return totalSteps;

        int minSteps(int sourceRow, int sourceColumn, int targetRow, int targetColumn)
        {
            HashSet<(int, int)> visited = new();
            Queue<(int, int)> queue = new();

            queue.Enqueue((sourceRow, sourceColumn));

            int steps = 0;

            while (queue.Count > 0)
            {
                int layerLength = queue.Count;

                for (int i = 0; i < layerLength; i++)
                {
                    (int row, int column) = queue.Dequeue();

                    if (row == targetRow && column == targetColumn) return steps;

                    foreach (var direction in directions)
                    {
                        int nextRow = row + direction.dR;
                        int nextColumn = column + direction.dC;

                        if (nextRow < 0 || nextRow >= rows || nextColumn < 0 || 
                            nextColumn >= columns || forest[nextRow][nextColumn] == BLOCK) continue;

                        if (visited.Contains((nextRow, nextColumn))) continue;

                        visited.Add((nextRow, nextColumn));
                        queue.Enqueue((nextRow, nextColumn));
                    }
                }

                steps++;
            }

            return -1;            
        }        
    }
}
