		vis=set([i,j])
		q=deque([[i,j,0]])
		while q:
			x,y,d=q.pop()
			if forest[x][y]==v:
				return x,y,d
			for i,j in [(x+1,y),(x-1,y),(x,y+1),(x,y-1)]:
				if 0<=i<n and 0<=j<m and not (i,j) in vis and forest[i][j]:
					vis.add((i,j))
					q.appendleft([i,j,d+1])
		return -1,-1,-1

	def cutOffTree(self, forest: List[List[int]]) -> int:
		order=[]
		n,m=len(forest),len(forest[0])
		for i in range(n):
			for j in range(m):
				if forest[i][j]>1:
					order.append(forest[i][j])
		order.sort()
		res,x,y=0,0,0
		for num in order:
			x,y,d=self.bfs(num,x,y,n,m,forest)
			if d==-1:
				return -1
			else:
				res+=d
		return res

