class Solution {
public:
    int cutOffTree(vector<vector<int>>& f) {
        vector<int> trees;
        for (auto& v : f) {
            for (int x : v) {
                if (x > 1)
                    trees.push_back(x);
            }
        }
        sort(trees.begin(), trees.end());
        int treecounter = 0;
        int visited[51][51] = { 0 };
        queue<tuple<int, int, int>> q;
        q.push({0, 0, trees[0]});
        vector<vector<int>> dirs = {{-1 , 0}, {0, -1}, {1, 0}, {0, 1}};
        int steps = 0;
        while (q.size()) {
            int size = q.size();
            while (size--) {
                auto[x, y, t] = q.front();
                q.pop();
                // Discontinue this search as the corresponding
                // tree is no more
                if (trees[treecounter] != t)
                    continue;
                // Found tree; cut tree
                if (f[x][y] == t) {
                    f[x][y] = 1;
                    treecounter++;
                    if (treecounter == trees.size())
                        return steps;
                    // Next tree to target
                    t = trees[treecounter];
                }
                for (auto& d : dirs) {
                    int nx = x + d[0];
                    int ny = y + d[1];
                    if (nx < 0 || ny < 0 || nx == f.size() || ny == f[0].size())
                        continue;
                    if (f[nx][ny] == 0)
                        continue;
                    // If we've been here before looking for the same tree
                    if (visited[nx][ny] == t)
                        continue;
                    visited[nx][ny] = t;
                    q.push({nx, ny, t});
                }
            }
            steps++;
        }
        return -1;
    }
};
