public int shortestPathBfs(int[] start, int[] end, List<List<Integer>> forest) {
        int n = forest.size();
        int m = forest.get(0).size();
        boolean[][] visited = new boolean[n][m];
        for(int i = 0; i<n; i++) {
            for(int j = 0; j<m; j++) {
                visited[i][j] = false;
            }
        }

        int[][] matrixTraversal = {{0,-1}, {-1, 0}, {0, 1}, {1, 0}};
        Queue<int[]> bfs = new LinkedList<>();
        bfs.add(new int[]{start[0], start[1], 0});
        visited[start[0]][start[1]] = true;

        while(!bfs.isEmpty()) {
            int[] tree = bfs.poll();
            if(tree[0] == end[0] && tree[1] == end[1]) {
                return tree[2];
            }

            /*
                marking visited as true in loop only as we will be reaching the node in the shortest distance in first visit itself.
             */
            for(int i = 0; i<4; i++) {
                int x = tree[0] + matrixTraversal[i][0];
                int y = tree[1] + matrixTraversal[i][1];
                if(x >=0 && y >=0 && x<n && y<m && forest.get(x).get(y)!=0 && !visited[x][y]) {
                    bfs.add(new int[]{x, y, tree[2] + 1});
                    visited[x][y] = true;
                }
            }
        }

        return -1;
    }



    public int cutOffTree(List<List<Integer>> forest) {
        int n = forest.size();
        int m = forest.get(0).size();
        PriorityQueue<int[]> trees = new PriorityQueue<>((a,b) -> {
            if(a[2] > b[2]) {
                return 1;
            }
            return -1;
        });

        for(int i = 0; i<n; i++) {
            for(int j = 0; j<m; j++) {
                if(forest.get(i).get(j) > 1) {
                    trees.add(new int[]{i, j, forest.get(i).get(j)});
                }
            }
        }

        int[] start = new int[]{0, 0, forest.get(0).get(0)};
        int totalSteps = 0;
        while(!trees.isEmpty()) {
            int[] end = trees.poll();
            int steps = shortestPathBfs(start, end, forest);
            if(steps == -1) {
                return -1;
            }
            totalSteps += steps;

            start[0] = end[0];
            start[1] = end[1];
        }

        return totalSteps;
    }
