class Solution(object):
    def cutOffTree(self, forest):
        """
        :type forest: List[List[int]]
        :rtype: int
        """
        def bfs(i, j, destx, desty):
            q = deque()
            visited = set()
            q.append((i, j, 0))
            while q:
                x, y, step = q.popleft()
                if x == destx and y == desty:
                    return step
                for dirx, diry in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                    curx, cury = dirx + x, diry + y

                    if 0 <= curx < m and 0 <= cury < n and forest[curx][cury] != 0 and (curx, cury) not in visited:
                        q.append((curx, cury, step + 1))
                        visited.add((curx, cury))
            return -1


        m, n = len(forest), len(forest[0])

        heap = []
        for i in range(m):
            for j in range(n):
                if forest[i][j] > 1:
                    heapq.heappush(heap, (forest[i][j], i, j))

        res = 0
        i, j = 0, 0
        while heap:
            _, destx, desty = heapq.heappop(heap)
            step = bfs(i, j, destx, desty)
            # print i, j, destx, desty, step
            if step < 0:
                return -1
            res += step
            i, j = destx, desty
        return res
