public class Solution {

    private readonly IList<int[]> _directions = new List<int[]>()
    {
        new int[] { 1, 0 },
        new int[] { -1, 0 },
        new int[] { 0, 1 },            
        new int[] { 0, -1 }
    };

    public int CutOffTree(IList<IList<int>> forest) {

        if (forest == null)
        {
            throw new ArgumentNullException("Parameter cannot be null", nameof(forest));
        }

        return CutOffTreeBfs(forest);
    }

    private int CutOffTreeBfs(IList<IList<int>> forest)
    {
        // Note that here we will store <height:coordinates>
        var treeMap = new Dictionary<int, int[]>();
        for (var row = 0; row < forest.Count; row++)
        {
            for (var col = 0; col < forest[0].Count; col++)
            {
                // Note that we need to store only trees of height greater than one, since anything else is not a tree and either just grass or a barrier
                if (forest[row][col] > 1)
                {
                    // Note that we are guaranteed unique heights
                    treeMap.Add(forest[row][col], new int[] { row, col });
                }
            }
        }

        var result = 0;
        var startRow = 0;
        var startCol = 0;
        var sortedTreeMap = treeMap.OrderBy(kvp => kvp.Key);

        foreach (var kvp in sortedTreeMap)
        {
            var endRow = kvp.Value[0];
            var endCol = kvp.Value[1];

            var steps = GetShortestPathBfs(forest, startRow, startCol, endRow, endCol);
            if (steps == -1)
            {
                return -1;
            }

            result += steps;
            startRow = endRow;
            startCol = endCol;
        }

        return result;
    }

    private int GetShortestPathBfs(IList<IList<int>> forest, int startRow, int startCol, int endRow, int endCol)
    {
        if (startRow == endRow && startCol == endCol)
        {
            return 0;
        }

        var queue = new Queue<int[]>();
        queue.Enqueue(new int[] { startRow, startCol });

        var visited = new int[forest.Count, forest[0].Count];
        visited[startRow, startCol] = 1;

        var level = 0;

        while (queue.Count > 0)
        {
            level++;
            var size = queue.Count;
            while (size > 0)
            {
                var oldNode = queue.Dequeue();
                var oldRow = oldNode[0];
                var oldCol = oldNode[1];

                foreach (var d in _directions)
                {
                    var newRow = oldRow + d[0];
                    var newCol = oldCol + d[1];

                    if (newRow >= 0 && newRow < forest.Count && newCol >= 0 && newCol < forest[0].Count && forest[newRow][newCol] != 0 && visited[newRow, newCol] == 0)
                    { 
                        if (newRow == endRow && newCol == endCol)
                        {
                            return level;
                        }                        

                        visited[newRow, newCol] = 1;                        
                        queue.Enqueue(new int[] { newRow, newCol });
                    }
                }

                size--;
            }
        }

        return -1;
    }

}
