class Solution {
    int n,m;
    int d[4][2] = {{-1,0},{0,-1},{0,1},{1,0}};
public:

    bool valid(int a,int b,int n,int m){

        return (a>=0&&a<n&&b>=0&&b<m);
    }

    int bfs(vector<vector<int>> &fo,pair<int,int> start,pair<int,int> target){

        if(start.first==target.first && start.second==target.second)
            return 0;

        vector<vector<bool>> used(n,vector<bool>(m,false));
        used[start.first][start.second] = true;

        int steps = 0;
        queue<pair<int,int>> q;

        q.push(start);
        while(!q.empty()){

            steps++;
            int size = q.size();

            while(size--){
                auto curr = q.front();
                q.pop();

                for(int i=0;i<4;i++){
                    int x = curr.first+d[i][0];
                    int y = curr.second+d[i][1];

                    if(valid(x,y,n,m) && !used[x][y] && fo[x][y]>0){

                        if(x==target.first && y==target.second)
                            return steps;

                        used[x][y] = true;
                        q.push({x,y});   
                    }
                }
            }
        }
        return -1;
    }

    int cutOffTree(vector<vector<int>>& forest) {

        n = forest.size();
        m = forest[0].size();

        if(n==0)
            return -1;

        typedef tuple<int,int,int> hxy;
        priority_queue<hxy,vector<hxy>,greater<hxy>> pq;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(forest[i][j]>1) 
                    pq.push({forest[i][j],i,j});
            }
        }

        int ans = 0;
        pair<int,int> start{0,0};
        pair<int,int> target;

        while(!pq.empty()){
            auto curr = pq.top();
            pq.pop();

            target = {get<1>(curr),get<2>(curr)};
            int steps = bfs(forest,start,target);

            if(steps==-1)
                return -1;

            ans+=steps;
            start = target;
        }


        return ans;
    }
};

