class Solution {
public:
    int cutOffTree(vector<vector<int>>& forest) {
        if(!forest.size() || !forest[0].size()) return 0;
        int n = forest.size();
        int m = forest[0].size();
        vector<int> trace;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                trace.push_back(forest[i][j]);
            }
        }
        sort(trace.begin(),trace.end());
        int curI=0,curJ=0;
        int steps = 0;
        for(auto i:trace){
            if(i==0||i==1)  continue;
            int ans = bfs(curI,curJ,forest,i,n,m);
            if(ans==-1) return -1;
            steps+=ans; 
        }
        return steps;
    }
private:
    int bfs(int &curi, int &curj,vector<vector<int>> &forest, int dest,int n, int m){
        if(forest[curi][curj]==dest)  return 0;
        vector<vector<int>> visited(n,vector<int>(m,0));
        visited[curi][curj]=1;
        list<pair<pair<int,int>,int>> q; //i,j, steps
        q.push_back({{curi,curj},0});
        while(!q.empty()){

            int i = q.front().first.first;
            int j = q.front().first.second;
            int steps = q.front().second;
            q.pop_front();
            if(forest[i][j]==dest){
                forest[i][j]=1;
                curi=i, curj=j;
                return steps;
            }
            if(i+1<n && visited[i+1][j]==0 && forest[i+1][j]!=0){
                visited[i+1][j]=1;
                q.push_back({{i+1,j},steps+1});
            }
            if(j+1<m && visited[i][j+1]==0 && forest[i][j+1]!=0){
                visited[i][j+1]=1;
                q.push_back({{i,j+1},steps+1});
            }
            if(i-1>=0 && visited[i-1][j]==0 && forest[i-1][j]!=0){
                visited[i-1][j]=1;
                q.push_back({{i-1,j},steps+1});
            }
            if(j-1>=0 && visited[i][j-1]==0 && forest[i][j-1]!=0){
                visited[i][j-1]=1;
                q.push_back({{i,j-1},steps+1});
            }
        }
        return -1;
    }
};
