solutions.content:
Saw a lot of bfs, think I might share my A* shortest path algoritm 
Basic idea is as follow
1. Sort the trees accroding to height, this will be the cut off sequence.
2. Find all the shortest path (A* algorithm) starting from 0,0 to the shortest tree, then keeping going to the next taller till the tallest tree.
```
class Node{
  public:
    int value;
    int row;
    int col;
    int fromStart;
    int toEnd;

    Node(int row, int col, int value){
        this->value = value;
        this->row = row;
        this->col = col;
        this->fromStart = -1;
        this->toEnd = -1;
    }
};
class Solution {
public:
    int cutOffTree(vector<vector<int>>& forest) {
        vector<vector<int>> heights;
        vector<vector<Node*>> g(forest.size(),vector<Node*>(forest[0].size(), NULL));
		//building the A* matrix
        for(int i = 0; i < forest.size();i ++){
            for(int j = 0; j < forest[0].size();j++){
                g[i][j] = new Node(i,j,forest[i][j]);
                if(forest[i][j] > 1) heights.push_back({forest[i][j], i,j});
            }
        }
		//sort the tree acording to height
        sort(heights.begin(),heights.end(),[](vector<int>& v1,vector<int>& v2){return v1[0] < v2[0];});
        vector<vector<bool>> seen(forest.size(),vector<bool>(forest[0].size(), false));
		//from starting point to the shortest tree
        int steps = shortestPath(0,0,heights[0][1],heights[0][2], g,seen);
        if(steps == -1) return -1;
        for(int i = 1; i < heights.size();i++){
			//find all the shortest path between each tree in the cuting sequence 
            int step = shortestPath(heights[i-1][1], heights[i-1][2],heights[i][1],heights[i][2], g,seen);
			//if no path availabe
            if(step == -1) return -1;
            steps += step;
        }
        return steps;
    }

    int getDist(int x1, int y1, int x2, int y2){
        return abs(x1-x2) + abs(y1-y2);
    }

	//This is pretty standard A* impelmentation , can refer to any materials online
    int shortestPath(int startRow, int startCol, int endRow, int endCol, vector<vector<Node*>>& g,vector<vector<bool>>& seen ){
         for(int i = 0; i < g.size();i ++){
            for(int j = 0; j < g[0].size();j++){
                g[i][j]->fromStart = -1;
                g[i][j]->toEnd = -1;
                seen[i][j] = false;
            }
        }
        vector<int> rowDiff = {-1,1,0,0};
        vector<int> colDiff = {0,0,-1,1};
        auto comp = [](Node* n1, Node* n2){
            if(n2->fromStart + n2->toEnd == n1->fromStart + n1->toEnd){
                return n1->fromStart > n2->fromStart;
            }else{
                return n1->fromStart + n1->toEnd > n2->fromStart + n2->toEnd;
            }
        };
        priority_queue<Node*, vector<Node*>, decltype(comp)> pq(comp);
        g[startRow][startCol]->fromStart = 0;
        g[startRow][startCol]->toEnd = getDist(startRow,startCol,endRow,endCol);
        pq.push(g[startRow][startCol]);
        while(!pq.empty()){
            Node* curr = pq.top();
            pq.pop();
            if(seen[curr->row][curr->col]) continue;
            seen[curr->row][curr->col] = true;
            if(curr->row == endRow && curr->col == endCol) return curr->fromStart;
            for(int i = 0; i < 4; i++){
                int nextRow = curr->row + rowDiff[i];
                int nextCol = curr->col + colDiff[i];

