class Solution {
public:
    vector<int> d = {0, 1, 0, -1, 0};
    int minSteps(vector<vector<int>> &f, array<int, 2> src, array<int, 2> dest) {
        vector<vector<int>> vis(f.size(), vector<int>(f[0].size(), 0));
        queue<array<int, 2>> q;
        q.push(src);
        vis[src[0]][src[1]] = 1;
        int moves = 0;
        while(!q.empty()) {
            int size = q.size();
            while(size--) {
                auto top = q.front();
                q.pop();
                if(top == dest) return moves;
                for(int i = 0; i < 4; i++) {
                    int ni = top[0] + d[i], nj = top[1] + d[i + 1];
                    if(ni < 0 || nj < 0 || ni >= f.size() || nj >= f[0].size() || vis[ni][nj] || !f[ni][nj]) continue;
                    vis[ni][nj] = 1;
                    q.push({ni, nj});
                }
            }
            moves++;
        }
        return -1;
    }
    int cutOffTree(vector<vector<int>>& f) {
        int m = f.size(), n = f[0].size();
        auto cmp = [&](auto &a, auto &b) {
            return f[a[0]][a[1]] > f[b[0]][b[1]];
        };
        priority_queue<array<int, 2>, vector<array<int, 2>>, decltype(cmp)> pq(cmp);
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(!f[i][j] || f[i][j] == 1) continue;
                pq.push({i, j});
            }
        }
        int steps = 0;
        array<int, 2> last = {0, 0};
        while(!pq.empty()) {
            int res = minSteps(f, last, pq.top());
            if(res == -1) return -1;
            steps += res;
            last = pq.top();
            pq.pop();
        }
        return steps;
    }
};
