public class Solution {
    public int CutOffTree(IList<IList<int>> forest) {
        var rows = forest.Count;
        var ans = 0;
        if(rows==0 || forest[0].Count == 0){
            return ans;
        }
        var cols = forest[0].Count;

        var trees = new List<List<int>>();
        for(int i=0;i<rows;i++){
            for(int j=0;j<cols;j++){
                if(forest[i][j]>0){
                    trees.Add(new List<int>{forest[i][j],i,j});
                }
            }
        }
        trees.Sort((a,b)=>a[0]-b[0]);
        var row = 0;
        var col = 0;
        foreach(var tree in trees){
            var res = ShortestPath(forest,row,col,tree[1],tree[2]);
           // Console.WriteLine(res+" "+tree[0]);
            if(res==-1){
                return -1;
            }
            ans+=res;
            row = tree[1];
            col = tree[2];
        }

        return ans;
    }

    private int ShortestPath(IList<IList<int>> forest, int i,int j, int fr,int fc){
        var m = forest.Count;
        var n = forest[0].Count;
        var visited = new bool[m,n];
        var queue = new Queue<int[]>();
        queue.Enqueue(new int[]{i,j});
        var xDir = new int[]{0,0,1,-1};
        var yDir = new int[]{1,-1,0,0};
        var ans = 0;
        while(queue.Count!=0){
            var size = queue.Count;

            for(int k=0;k<size;k++){
                var top = queue.Dequeue();
                if(top[0]==fr && top[1]==fc){
                    return ans;
                }
                for(int l=0;l<4;l++){
                    var nx = top[0]+xDir[l];
                    var ny = top[1]+yDir[l];
                    if(nx>=m || ny>=n || nx<0 || ny<0 || forest[nx][ny]==0 || visited[nx,ny]){
                        continue;
                    }
                    queue.Enqueue(new int[]{nx,ny});
                    visited[nx,ny] = true;
                }
            }
            ans++;
        }


        return -1;
    }
}
