class Solution {
   public int cutOffTree(List<List<Integer>> forest) {

		if (forest == null || forest.size() == 0) {

			return 0;
		}

		PriorityQueue<Place> placeQ = new PriorityQueue<>(
				Comparator.comparingInt(Place::getValue));

		for (int row = 0; row < forest.size(); row++) {
			for (int col = 0; col < forest.get(0).size(); col++) {
                Place place = new Place(row, col, forest.get(row).get(col));
                if(!place.isObs()){

                    placeQ.add(place);
                }
			}
		}

		int totalSteps = 0;

		Place cur = new Place(0,0,forest.get(0).get(0));

		while (!placeQ.isEmpty()) {

			Place next = placeQ.poll();

			int steps = searchPlace(cur, next, forest);

			if (steps == -1) {
				return -1;
			}
			totalSteps += steps;

			cur = next;
		}

		return totalSteps == 0 ? -1 : totalSteps;
	}

	private int searchPlace(Place start, Place end,
			List<List<Integer>> forest) {

		if (start.equals(end)) {

			return 0;
		}

		LinkedList<Place> places = new LinkedList<>();
		places.add(start);

		int[][] counter = new int[forest.size()][forest.get(0).size()];
        boolean[][] visited = new boolean[forest.size()][forest.get(0).size()];
		visited[start.row][start.col] = true;
        counter[start.row][start.col] = 0;

		while (!places.isEmpty()) {

			Place cur = places.poll();

			List<Place> neighbours = getNeighbours(cur.row, cur.col, forest, visited);

			for (Place p : neighbours) {

				if (visited[p.row][p.col]) {

					continue;
				}

                counter[p.row][p.col] = counter[cur.row][cur.col] +1;

				if (p.equals(end)) {

					return counter[p.row][p.col];
				}

                visited[p.row][p.col] = true;

				places.add(p);   
			}
		}

		return -1;
	}

	private List<Place> getNeighbours(int row, int col,
			List<List<Integer>> forest, boolean[][] visited) {

		List<Place> places = new ArrayList<>();

		getLeft(row, col, forest, places, visited);

		getRight(row, col, forest, places, visited);

		getTop(row, col, forest, places, visited);

		getBottom(row, col, forest, places, visited);

		return places;
	}

	private void getLeft(int row, int col, List<List<Integer>> forest, List<Place> places, boolean[][] visited) {

        col = col -1;

		if (col < 0 || visited[row][col]) {

			return;
		}

		Place place = new Place(row, col, forest.get(row).get(col));

		if (!place.isObs()) {

			places.add(place);
		}
	}

	private void getRight(int row, int col, List<List<Integer>> forest, List<Place> places, boolean[][] visited) {

        col = col +1;

		if (col >= forest.get(0).size() || visited[row][col]) {

			return;
		}


		Place place = new Place(row, col, forest.get(row).get(col));

		if (!place.isObs()) {

			places.add(place);
		}
	}

	private void getTop(int row, int col, List<List<Integer>> forest, List<Place> places, boolean[][] visited) {

        row = row - 1;

        if (row < 0 || visited[row][col]) {

			return;
		}

		Place place = new Place(row, col, forest.get(row).get(col));

		if (!place.isObs()) {

			places.add(place);
		}
	}

	private void getBottom(int row, int col, List<List<Integer>> forest, List<Place> places, boolean[][] visited) {

        row = row +1;

		if (row >= forest.size() || visited[row][col]) {

			return;
		}

		Place place = new Place(row, col, forest.get(row).get(col));

		if (!place.isObs()) {

			places.add(place);
		}
	}
}


class Place {
	int row;
	int col;
	int value;
	boolean visited;

	Place(int row, int col, int value) {
		this.row = row;
		this.col = col;
		this.value = value;
	}

	boolean isObs() {

		return value == 0;
	}

    public int getValue(){

        return value;
    }

	public boolean equals(Object o) {

		Place that = (Place) o;

		return this.row == that.row && this.col == that.col;
