class Solution {
    public int cutOffTree(List<List<Integer>> forest) {

        int width = forest.size();
        int height = forest.get(0).size();

        // [positionX, positionY, Value]
        List<int[]> trees = new ArrayList();

		// at first, we need a list trees with their coordinates to be able go to them one-by-one in ascending order
        for(int x = 0; x < width; x++) {
            for(int y = 0; y < height; y++) {
                int val = forest.get(x).get(y);
                if(val > 1) {
                    trees.add(new int[] {x, y, val});
                }
            }
        }

        // sort tree list ascending
        Collections.sort(trees, (a, b) -> a[2] - b[2] );

        // all steps (answer)
        int steps = 0;

        // current position. We start form [0, 0]
        int[] currentPosition = new int[] {0, 0};

        for(int t = 0; t < trees.size(); t++) {
            int[] tree = trees.get(t);
            int treeX = tree[0];
            int treeY = tree[1];

            int[] treePosition = new int[] {treeX, treeY};
            int needSteps = howManyStepsDoWeNeed(currentPosition, treePosition, forest);

            // if there is no way to the next tree
            if(needSteps == -1) {
                return -1;
            }

            // cut the tree
            forest.get(treeX).remove(treeY);
            forest.get(treeX).add(treeY, 1); // "1" is grass

            // walk to the next tree
            currentPosition = treePosition;

            steps += needSteps;
        }

        return steps;
    }

    /**
    * Looking for the shortes path from `currentPosition` to `treePosition`
    * BFS
    **/
    private int howManyStepsDoWeNeed(int[] currentPosition, int[] treePosition, List<List<Integer>> forest) {
        int width = forest.size();
        int height = forest.get(0).size();

        // [positionX, positionY]
        Queue<Pair<Integer, Integer>> queue = new LinkedList();
        Set<Pair<Integer, Integer>> visited = new HashSet();

        // First step (start point)
        queue.add(new Pair(currentPosition[0], currentPosition[1]));
        visited.add(new Pair(currentPosition[0], currentPosition[1]));

        // calculate how many steps do we need to reach the tree
        int steps = 0;
        int stepsForThisTreeLevel = queue.size();

        while(!queue.isEmpty()) {
            stepsForThisTreeLevel--;

            // current point
            Pair<Integer, Integer> point = queue.poll();

            // current position
            int x = point.getKey();
            int y = point.getValue();

            // if we are on the same coordintates as tree that we need to
            if(x == treePosition[0] && y == treePosition[1]) {
                return steps;
            }

            // step right
            if(x + 1 < width) {
                Pair<Integer, Integer> newStep = new Pair(x + 1, y);
                if(!visited.contains(newStep) && canWalk(newStep, forest)) {
                    visited.add(newStep);
                    queue.add(newStep);
                }
            }
            // step left
            if(x - 1 >= 0) {
                Pair<Integer, Integer> newStep = new Pair(x - 1, y);
                if(!visited.contains(newStep) && canWalk(newStep, forest)) {
                    visited.add(newStep);
                    queue.add(newStep);
                }
            }
            // step bottom
            if(y + 1 < height) {
                Pair<Integer, Integer> newStep = new Pair(x, y + 1);
                if(!visited.contains(newStep) && canWalk(newStep, forest)) {
                    visited.add(newStep);
                    queue.add(newStep);
                }
            }
            // step top
            if(y - 1 >= 0) {
                Pair<Integer, Integer> newStep = new Pair(x, y - 1);
                if(!visited.contains(newStep) && canWalk(newStep, forest)) {
                    visited.add(newStep);
                    queue.add(newStep);
                }
            }

            // new level of tree (how many steps we've done)
            if(stepsForThisTreeLevel == 0) {
                stepsForThisTreeLevel = queue.size();
                steps++;
            }
        }

        // if we didn't find a right path
        return -1;
    }

    private boolean canWalk(Pair<Integer, Integer> position, List<List<Integer>> forest) {
        return forest.get(position.getKey()).get(position.getValue()) >= 1;
    } 
}
