'''
1. map the tree heights in ascending order to their position, hi: (ri, ci)
2. find shortest distance to (ri, ci)... if shortest distance = -1, return -1 immediately
3. use bfs for calculating shortest distance
'''
class Solution:
    def __init__(self):
        self.dirs = ((0,1), (0, -1), (1, 0), (-1, 0))
        self.n, self.m = 0, 0

    def cutOffTree(self, forest: List[List[int]]) -> int:
        self.n, self.m = len(forest), len(forest[0])
        l = []
        for i, row in enumerate(forest):
            for j, val in enumerate(row):
                if val > 1:
                    l.append((val, (i, j)))
        l.sort(key = lambda x: x[0])
        at, ret = (0, 0), 0
        for _, to in l:
            distance = self.shortest_distance(at, to, forest)
            if distance == -1:
                return -1
            ret += distance
            at = to
        return ret

    def shortest_distance(self, at, to, forest):
        if at == to:
            return 0
        ret = 0
        q = deque()
        q.append(at)
        vis = set()
        vis.add(at)
        while q:
            for _ in range(len(q)):
                cur = q.popleft()
                for dx, dy in self.dirs:
                    x, y = cur[0] + dx, cur[1] + dy
                    if x < 0 or x >= self.n or y < 0 or y >= self.m or (x, y) in vis or forest[x][y] == 0:
                        continue
                    if (x, y) == to:
                        return ret + 1
                    else:
                        q.append((x, y))
                        vis.add((x, y))
            ret += 1
        return -1
