"""
alg:
1. find all trees and their coordinates, sorted by height ascending
2. BFS from 0,0 to min's coordinate; from min to sec min and so on; record steps taken
3. returns -1 if blocked
4. return total steps
"""
from collections import deque
class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        def bfs(start, stop):
            if start == stop:
                return 0

            queue = deque([start])
            directions = [(0,1), (0,-1), (1,0), (-1,0)]
            steps = 0
            visited = set()
            visited.add(start)
            while queue:
                size = len(queue)
                steps += 1
                while size:
                    x, y = queue.popleft()
                    for dx, dy in directions:
                        if 0<=x+dx<rows and 0<=y+dy<cols and forest[x+dx][y+dy] and (x+dx, y+dy) not in visited:
                            if x+dx == stop[0] and y+dy == stop[1]:
                                return steps
                            queue.append((x+dx, y+dy))
                            visited.add((x+dx, y+dy))
                    size -= 1
            return -1


        rows = len(forest)
        cols = len(forest[0])
        trees = []
        for i in range(rows):
            for j in range(cols):
                if forest[i][j] > 1:
                    trees.append((forest[i][j], (i, j)))
        trees.sort(key=lambda x: x[0])
        checkpoints = [(0, 0)] + [tree[1] for tree in trees]

        steps = 0
        for i in range(len(checkpoints)-1):
            cur_step = bfs(checkpoints[i], checkpoints[i+1])
            if cur_step != -1:
                steps += cur_step
            else:
                return -1

        return steps
