class Solution {
public:
    int cutOffTree(vector<vector<int>>& forest) {
        R = forest.size(); C = forest[0].size();
        v.clear();
        for (int i = 0; i < forest.size(); i++) {
            for (int j = 0; j < forest[i].size(); j++) {
                if (forest[i][j] > 1) {
                    Node n={i, j, forest[i][j]}; 
                    v.push_back(n);
                }                
            }
        }
        sort(v.begin(), v.end());
        int ans = 0;
        Node cur={0, 0, 1};
        for (int i = 0; i < v.size(); i++) {
            int step = hadlock(forest, cur.r, cur.c, v[i].r, v[i].c);
            if (step < 0) {
                return -1;
            }

            ans += step;
            cur = v[i];
        }
        return ans;
    }
private:
    int R, C;
    int hadlock(vector<vector<int>>& forest, int sr, int sc, int tr, int tc) {    
        dqf=dqb=dq+500;
        Node cur={sr, sc, 0};
        *dqb=cur;
        ++dqb;
        memset(mask,0,R*C);
        while (dqf!=dqb) {
            Node n = *dqf;
            ++dqf;
            if (mask[n.r*C+n.c]) {
                continue;
            }
            mask[n.r*C+n.c] = true;
            if (n.r == tr && n.c == tc) {
                return abs(sr - tr) + abs(sc - tc) + 2*n.d;
            }
            for (int i = 0; i < 4; i++) {
                int nr = n.r + direct[i][0];
                int nc = n.c + direct[i][1];
