                    bool closer = abs(n.r - tr) > abs(nr - tr) || abs(n.c - tc) > abs(nc - tc);
                    if (closer) {
                        *(--dqf)={nr, nc, n.d};
                    }
                    else {
                        *(dqb++)={nr, nc, n.d+1};
                    }
                }
            }
        }

        return -1;
    }
};
