class Solution:
    def cutOffTree(self, forest):
        """
        :type forest: List[List[int]]
        :rtype: int
        """
        def neighbors(i, j, forest, tree_idx, visited):
            m, n = len(forest), len(forest[0])
            for x, y in [[i-1, j], [i+1, j], [i, j-1], [i, j+1]]:
                if 0 <= x < m and 0 <= y < n and forest[x][y] and (x, y) not in visited:
                    yield x, y

        if not forest or not forest[0] or not forest[0][0]:
            return -1
        trees = [col for row in forest for col in row if col > 1]
        trees.sort()
        queue = collections.deque([[0, 0, -1, 0]])
        visited = {(0, 0)}
        while queue:
            size = len(queue)
            for _ in range(size):
                i, j, tree_idx, steps = queue.popleft()
                if tree_idx == len(trees) - 1:
                        return steps
                if forest[i][j] == trees[tree_idx+1]:
                    tree_idx += 1
                    queue = collections.deque([[i, j, tree_idx, steps]])
                    visited = {(i, j)}
                    break
                for x, y in neighbors(i, j, forest, tree_idx, visited):
                    queue.append([x, y, tree_idx, steps+1])
                    visited.add((x, y))
        return -1
