    int cutOffTree(vector<vector<int>>& forest) {

        // Given : Clear the trees in increasing order of height
        // constraints : n , m <= 50 = Larger Complexity Acceptable

        // Solution : To handle the contriant we are given that we have to cut them
        // in increasing order which provides us with solution itself

        // If gives tress are [ 10 , 12 , 11 ]
        // if i cut 10 next i have to cut will be 11 and i need to find a way to reach 11 
        // if possible find steps to it if not return -1

        vector<vector<int>> trees;
        for(int i = 0; i < forest.size(); i++){

            for(int j = 0; j < forest[0].size(); j++){

                if(forest[i][j] > 1){

                    trees.push_back({forest[i][j] , i , j});
                }
            }
            cout<<endl;
        }

        // sorting the trees according to their height
        sort(trees.begin() , trees.end());

        // how it gurantees minimum ? As bfs does a level order search so we will
        // surely get the closest step automatically

        // defining all valid moves in advance
        vector<pair<int,int>> moves = {{0 , 1} , {1 , 0} , { 0 , -1} , {-1 , 0}};

        // defining start points 
        int sx = 0 , sy = 0;
        int ans = 0;
        // now we will cut trees in order
        for(int i = 0; i < trees.size(); i++){

            // using breadth first search to find if next tree is reachable or not
            int cost = bfs(forest , sx , sy , trees[i][1] , trees[i][2] , moves);
            // if not reachable return -1
            if(cost == -1){

                return -1;
            }
            // if reachable add the 1 to cost
            ans += cost;
            // update the start points to new tree
            sx = trees[i][1];
            sy = trees[i][2];

        }

        return ans;

    }

    int bfs(vector<vector<int>> forest , int sx , int sy , int fx , int fy , vector<pair<int,int>> &moves){

        // if we are search for start point
        if(fx == sx && fy == sy) return 0;

        // now let's do a bfs 

        queue<pair<int,int>> q;
        q.push({sx , sy});
        int n = forest.size() , m = forest[0].size();
        vector<vector<int>> seen(n , vector<int>(m , 0));
        seen[sx][sy] = 1;
        int cost = 0;
        while(!q.empty()){

            int qsize = q.size();
            // iterating the current level
            for(int i = 0; i < qsize; i++){

                pair<int,int> point = q.front();
                q.pop();

                for(int j = 0 ; j < moves.size(); j++){

                    long int newx = point.first + moves[j].first;
                    long int newy = point.second + moves[j].second;

                    // skip if new point is not valid point
                    if(newx >= n || newy >= m || newx < 0 || newy < 0 ) continue;

                    // if new point is not obstacle and also not seen before the add it
                    if(forest[newx][newy] != 0 && seen[newx][newy] == 0){

                        q.push({newx , newy});
                        seen[newx][newy] = 1;

                        if(newx == fx && newy == fy){

                            return cost + 1;
                        }

                    }

                }

            }

            cost++;


        }

        // if not found return -1
        return -1;


    }
};
