class Solution {
public:
    vector<pair<int,int>> directions={{-1,0},{0,-1},{0,1},{1,0}};
    int bfs(int startRow, int startCol, int targetRow, int targetCol, vector<vector<int>> &forest)
    {
        if(startRow==targetRow && startCol==targetCol) return 0;
        int rows=forest.size(), cols=forest[0].size();
        vector<vector<int>> vis(rows, vector<int> (cols,0));
        queue<pair<int,int>> q;
        q.push({startRow,startCol});
        vis[startRow][startCol]=1;
        int level=0;
        while(!q.empty())
        {
            level++;
            int sz=q.size();
            while(sz--)
            {
                int r=q.front().first, c=q.front().second;
                q.pop();
                for(auto it: directions)
                {
                    int dr=r+it.first, dc=c+it.second;
                    if(dr<0 || dc<0 || dr>=rows || dc>=cols || forest[dr][dc]==0 || vis[dr][dc]) continue;
                    if(dr==targetRow && dc==targetCol) return level;
                    vis[dr][dc]=1;
                    q.push({dr,dc});
                }
            }
        }
        return -1;
    }
    int cutOffTree(vector<vector<int>>& forest) 
    {
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<pair<int,pair<int,int>>>> q;
        int rows=forest.size(), cols=forest[0].size();
        for(int r=0; r<rows; r++)
        {
            for(int c=0; c<cols; c++)
            {
                if(forest[r][c]>1)
                {
                    q.push({forest[r][c],{r,c}});
                }
            }
        }
        int ans=0;
        int startRow=0, startCol=0;
        while(!q.empty())
        {
            int targetRow=q.top().second.first, targetCol=q.top().second.second;
            q.pop();
            int steps=bfs(startRow, startCol, targetRow, targetCol, forest);
            if(steps==-1) return -1;
            ans+=steps;
            startRow=targetRow, startCol=targetCol;
        }
        return ans;
    }
};
