class Solution {
public:
    bool valid(int  x,int y,int n,int m)
    {
        return x>=0 && y>=0 && x<n && y<m;
    }
    int bfs(int x,int y,vector<vector<int>>& forest,int target)
    {
        int n=forest.size();
        int m=forest[0].size();
        vector<vector<int>>vis(n,vector<int>(m,0));
        queue<pair<int,int>>q;
        q.push({x,y});
        vis[x][y]=1;
        int deli[]={0,0,1,-1};
        int delj[]={1,-1,0,0};
        int steps=0;
        while(!q.empty())
        {
           int sz=q.size();
           while(sz--)
           {
               auto temp=q.front();
               q.pop();
               int xx=temp.first;
               int yy=temp.second;
               if (forest[xx][yy]==target) return steps;
               for (int i=0;i<4;i++)
               {
                   int newx=xx+deli[i];
                   int newy=yy+delj[i];
                   if (valid(newx,newy,n,m) && forest[newx][newy]>0 && vis[newx][newy]==0)
                   {
                     vis[newx][newy]=1;
                     q.push({newx,newy});
                   }
               }
           }
           steps++;
        }
        return -1;
    }
    int cutOffTree(vector<vector<int>>& forest)
    {
        priority_queue<pii,vector<pii>,greater<pii>>pq;
        int n=forest.size();
        int m=forest[0].size();
        for (int i=0;i<n;i++)
        {
            for (int j=0;j<m;j++)
            {
                if (forest[i][j]>1) pq.push({forest[i][j],{i,j}});
            }
        }
        int startx=0;
        int starty=0;
        int ans=0;
        while(!pq.empty())
        {
            auto temp=pq.top();
            pq.pop();
            int target=temp.first;
            int dis=bfs(startx,starty,forest,target);
            if (dis==-1) return -1;
            ans+=dis;
            startx=temp.second.first;
            starty=temp.second.second;
        }
        return ans;
    }
};
