class Solution {
private:
    bool isSafe(int row, int col, int n, int m){
        if(row >= 0 && row < n && col >= 0 && col < m) return true;
        return false;
    }
    vector<pii> edges = {{-1,0},{1,0},{0,1},{0,-1}};
    int bfs(vector<vector<int>>& f, int sx, int sy, int ex, int ey){
        int n = f.size(); int m = f[0].size();
        vector<vector<int>> vis(n, vector<int>(m,0));
        queue<piii> q;
        q.push({0,{sx,sy}});
        vis[sx][sy] = 1;

        while(!q.empty()){
            int row = q.front().ss.ff; int col = q.front().ss.ss; int dist = q.front().ff; q.pop();
            if(row == ex && col == ey) return dist; 

            for(auto edge : edges){
                int nrow = row + edge.ff;
                int ncol = col + edge.ss;

                if(isSafe(nrow, ncol, n, m) && f[nrow][ncol] != 0 && !vis[nrow][ncol]){
                    q.push({dist+1, {nrow, ncol}});
                    vis[nrow][ncol] = 1;
                } 
            }
        }

