        sort(v.begin(), v.end());

        int res = 0;
        for(int i = 0; i < v.size()-1; i++){
            int sx = v[i].ss.ff; int sy = v[i].ss.ss; int ex = v[i+1].ss.ff; int ey = v[i+1].ss.ss;
            int temp = bfs(f, sx, sy, ex, ey);
            if(temp == -1) return -1;
            res += temp;
        }

        return res;
    }
};
